package com.example.pms.controller; import com.example.pms.payload.JwtResponse; import com.example.pms.payload.LoginRequest; import com.example.pms.payload.SignupRequest; import com.example.pms.service.AuthService; import org.springframework.http.ResponseEntity; import org.springframework.web.bind.annotation.*;
@RestController @RequestMapping("/api/auth") public class AuthController { private final AuthService authService; public AuthController(AuthService authService){ this.authService = authService; } @PostMapping("/signin") public ResponseEntity<JwtResponse> authenticateUser(@RequestBody LoginRequest loginRequest){ return ResponseEntity.ok(authService.authenticateUser(loginRequest)); } @PostMapping("/signup") public ResponseEntity<String> registerUser(@RequestBody SignupRequest signUpRequest){ return ResponseEntity.ok(authService.registerUser(signUpRequest)); } }