package com.example.pms.controller; import com.example.pms.model.Project; import com.example.pms.service.ProjectService; import org.springframework.http.ResponseEntity; import org.springframework.web.bind.annotation.*;
@RestController @RequestMapping("/api/projects") public class ProjectController { private final ProjectService projectService; public ProjectController(ProjectService projectService){ this.projectService = projectService; } @PostMapping("") public ResponseEntity<Project> createProject(@RequestBody Project p){ return ResponseEntity.ok(projectService.createProject(p)); } @PostMapping("/{projectId}/addUser/{userId}") public ResponseEntity<String> addUser(@PathVariable Long projectId, @PathVariable Long userId){ projectService.addUserToProject(projectId, userId); return ResponseEntity.ok("User added"); } @GetMapping("/{id}") public ResponseEntity<Project> getProject(@PathVariable Long id){ return ResponseEntity.of(projectService.getProject(id)); } }